import RPi.GPIO as GPIO


class Button():
    """ Button class """

    def __init__(self, pin):
        """ Constructor """
        self._pin = pin
        GPIO.setmode(GPIO.BCM)
        GPIO.setup(self._pin, GPIO.IN, pull_up_down=GPIO.PUD_UP)

    def status(self):
        """ get status of button """
        if GPIO.input(self._pin) == GPIO.LOW:
            return True
        else:
            return False
