import time
import RPi.GPIO as GPIO


class Buzzer():
    """ Led class """

    def __init__(self, pin):
        """ Constructor """
        self._pin = pin
        self._on = False
        GPIO.setmode(GPIO.BCM)
        GPIO.setup(self._pin, GPIO.OUT)

    def on(self):
        GPIO.output(self._pin, GPIO.HIGH)
        self._on = True

    def off(self):
        GPIO.output(self._pin, GPIO.LOW)
        self._on = False

    def beep(self):
        self.on()
        time.sleep(0.11)
        self.off()
